index 094b3b0..cfd033b 100644
--- a/node_modules/@supabase/supabase-js/dist/main/SupabaseClient.js
+++ b/node_modules/@supabase/supabase-js/dist/main/SupabaseClient.js
@@ -81,6 +81,9 @@ class SupabaseClient {
